select script_id from iot_script

     and application_name like concat('%', #{applicationName}, '%')
     and scene_id = #{sceneId}
     and product_id = #{productId}
     and script_id = #{scriptId}
     and script_event = #{scriptEvent}
     and script_action = #{scriptAction}
     and script_purpose = #{scriptPurpose}
     and enable = #{enable}
     and script_language = #{scriptLanguage}
     and (user_id = #{userId} or user_id = #{tencentId})

order by script_order

mapper.update:
update iot_script
